    With lazy evaluation, all arguments to

      procedures
      functions

    are delayed uniformly.  For instance, we can implement

      procedures
      functions

    to integrate lists and solve differential equations as we originally
    intended in section:



      lazy_integral
      list_library_lazy
      2.715568520651728

(define (integral integrand initial-value dt)
  (define int
          (cons initial-value
                (add-lists (scale-list integrand dt)
                           int)))
  int)

      (define (solve f y0 dt)
      (define y (integral dy y0 dt))
      (define dy (map f y))
      y)


      ;;; L-Eval input:
      (list-ref (solve (lambda (x) x) 1 0.001) 1000)
      ;;; L-Eval value:
      2.716924


function integral(integrand, initial_value, dt) {	
    const int =
        pair(initial_value,
             add_lists(scale_list(integrand, dt),
                       int));
    return int;
}
function solve(f, y0, dt) {
    const y = integral(dy, y0, dt);
    const dy = map(f, y);
    return y;
}
list_ref(solve(x => x, 1, 0.001), 1000);


function integral(integrand, initial_value, dt) {	
    const int =
        pair(initial_value,
             add_lists(scale_list(integrand, dt),
                       int));
    return int;
}
function solve(f, y0, dt) {
    const y = integral(dy, y0, dt);
    const dy = map(f, y);
    return y;
}
list_ref(solve(x => x, 1, 0.002), 500);


